  return (
    <div className="">
      <div className="lg:max-w-2xl w-full xl:max-w-3xl mx-auto h-screen ">
        <div className="overflow-y-scroll scrollbar-hide justify-end h-full">
          {message.map((message) => (
            <Message
              key={message._id}
              message={message.message}
              name={message.name}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Chat;
